(* ============================================== *)
(* ============================================== *)
ClearAll["Global`*"];
(* ============================================== *)
(* ============================================== *)
maxChainLen=5;
useNNT=True;
useDirectCryst=True;
useActivation=True;
(*
rhoMult=1.352790779649687`;
LambdaMinusVal=0.3;
*)
rhoMult=1.260808123;
LambdaMinusVal=1.0;
(* ============================================== *)
runNDSolveVal=True;
(* ============================================== *)
(* EvRunTypeAll, EvRunTypeList, EvRunTypeRange, EvRunTypeNone *)
runAll=EvRunTypeAll;
(* ============================================== *)
partNo=2;
(* ============================================== *)
(* ============================================== *)
If[useDirectCryst,
(
(* usually smaller *)
tMaxVal=1.*10^5;
),
(
tMaxVal=1.*10^5;
)
];
(* ============================================== *)
(* ============================================== *)
silentRun=True;
(* ============================================== *)
PathList={"W:\\Math\\CLM\\"};
Get["CLM_Init.m",Path-> PathList];
LoadModules[PathList, silentRun];
(* ============================================== *)
(*
lstCatSedSigma={{1,23,False},{1,92,False},{1,109,False},{1,255,False},{1,257,False},{1,353,False},{1,355,False},{1,408,False},{1,410,False},{1,437,False},{1,439,False},{1,473,False},{1,12,True},{1,17,True},{1,40,True},{1,49,True},{1,70,True},{1,144,True},{1,161,True},{1,173,True},{1,175,True},{1,198,True},{1,212,True},{1,214,True},{1,233,True},{1,235,True},{1,284,True},{1,302,True},{1,304,True},{1,327,True},{1,329,True},{1,380,True},{1,382,True}};
*)

lstCatSedSigma={{1,1,False}};
(* ============================================== *)
MaxPartNo=8;
Switch[maxChainLen,
3,
(
MaxPartNo=1;
PartLength=28;
),
4,
(
PartLength=120/MaxPartNo;
),
5,
(
PartLength=496/MaxPartNo;
),
_,
(
Print["Unsupported maxChainLen = ", maxChainLen, "!"];
Quit[];
)
];
(* ============================================== *)
(*
substPairStart=11;
substPairEnd=12;
*)
substPairStart=1+(partNo-1)*PartLength;
substPairEnd=partNo*PartLength;

If[partNo==0,
(
substPairStart=1;
substPairEnd=1;
)
];

If[maxChainLen ==5 && partNo ==1,
(
substPairStart=2;
)
];

Print["maxChainLen = ",maxChainLen, ", partNo = ",partNo, ", substPairStart = ", substPairStart, ", substPairEnd = ", substPairEnd];
(* ============================================== *)
RoTotInitValGen=rhoMult*10^0;

UseRoTotalNormValGen=True;
(* ============================================== *)
(* ============================================== *)
(* EnantSelNoReaction, EnantSelNone, EnantSelPlus, EnantSelMinus, EnantSelAll, EnantSelPlusOne, EnantSelMinusOne *)
(* ============================================== *)
(* Catalytic synthesis *)
CatSynthEnantSel=EnantSelNoReaction;
InvCatSynthEnantSel=EnantSelNoReaction;
(* ============================================== *)
(* ============================================== *)
EvolutionInitialize[maxChainLen,useActivation,useNNT,useDirectCryst,runNDSolveVal];
(* ============================================== *)
γ0˖=0.01;
γ0˗=0.01;

Σ˖=10^3;
Σ˗=10^-2;
Δ˗=10^1;
ρ˗=10^-1;
ρ˖=10^2;

a˖=1;
a˗=10^-3;

Λ˖=1;
Λ˗=LambdaMinusVal;

(* ============================================== *)
(* ============================================== *)
(* OutputTypeNone, OutputTypeAll, OutputTypeCPandRho *)
OutputType$Eval=OutputTypeNone;
OutputMonitor$Distributions=False;
OutputMonitor$ChiralPolarization=False;
(* ============================================== *)
evrValues={Indeterminate, Indeterminate};

Switch[runAll,
EvRunTypeAll,
(
evrValues=EvolutionRunAll[tMaxVal];
),
EvRunTypeList,
(
PlotSubstancesValEval=True;
MaxDetailedOutputChainLen=3;
PrintInfoEval=True;

OutputType$Eval=OutputTypeCPandRho;
OutputMonitor$Distributions=True;
OutputMonitor$ChiralPolarization=True;

Σ˗˗=0;

evrValues=EvolutionRunList[lstCatSedSigma,tMaxVal];
),
EvRunTypeRange,
(
evrValues=EvolutionRunRange[tMaxVal,substPairStart,substPairEnd];
),
EvRunTypeNone,
(
Print["Not running..."];
),
_,
(
Print["Invalid value of runAll = ", runAll,"."];
)
];
(* ============================================== *)
Print["substDisplMatrix = ", substDisplMatrix];
Print[strSeparator];
Print["evrValues = ", Chop[evrValues]];
Print[strSeparator];
PrintTimeUsed[];
(* ============================================== *)
(* ============================================== *)

In[50]:= 
(* ============================================== *)
Print["maxChainLen = ",maxChainLen, ", partNo = ",partNo, ", substPairStart = ", substPairStart, ", substPairEnd = ", substPairEnd];
Print[strSeparator];
Print["substDisplMatrix = ", substDisplMatrix];
Print[strSeparator];
Print["evrValues = ", Chop[evrValues,chopDelta]];
Print[strSeparator];
PrintTimeUsed[];
(* ============================================== *)
Print["plusTbl = ", Chop[evrValues[[1]],chopDelta]// MatrixForm];
Print["minusTbl = ", Chop[evrValues[[2]],chopDelta]// MatrixForm];
